import React from 'react';
import styled from 'styled-components';

const Login = () => {
  return (
    <LoginContainer>
      <LoginForm>
        <LoginTitle>로그인</LoginTitle>
        <LoginInput
          type="text"
          placeholder="사용자 이름"
          required
        />
        <LoginInput
          type="password"
          placeholder="비밀번호"
          required
        />
        <LoginButton>로그인</LoginButton>
      </LoginForm>
    </LoginContainer>
  );
};

const LoginContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
`;

const LoginForm = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 400px;
  background-color: #fff;
  padding: 30px;
  border-radius: 5px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.2);
`;

const LoginInput = styled.input`
  width: 100%;
  padding: 10px;
  margin-bottom: 10px;
  border: 1px solid #ccc;
  border-radius: 3px;
  font-size: 16px;
  &:focus {
    outline: none;
    border-color: #007bff;
  }
`;

const LoginButton = styled.button`
  padding: 10px 20px;
  border: none;
  border-radius: 3px;
  background-color: #007bff;
  color: #fff;
  font-size: 16px;
  cursor: pointer;
`;

const LoginTitle = styled.h1`
  margin-bottom: 30px;
  font-size: 24px;
  font-weight: bold;
`;


export default Login;



