import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const EditProfile = () => {
  const [nickname, setNickname] = useState('');
  const [birthYear, setBirthYear] = useState('1990');
  const [birthMonth, setBirthMonth] = useState('01');
  const [birthDay, setBirthDay] = useState('01');

  const handleSubmit = (e) => {
    e.preventDefault();

    const profileData = {
      nickname,
      birthYear,
      birthMonth,
      birthDay
    };

    axios.post('/api/profile', profileData)
      .then(response => {
        console.log(response.data); // 수정이 성공했을 때의 응답 처리
      })
      .catch(error => {
        console.error(error); // 요청 실패 시의 에러 처리
      });
  };

  return (
    <Container>
      <Title>내 정보 수정</Title>
      <Form onSubmit={handleSubmit}>
        <FormGroup>
          <Label>이메일:</Label>
          <Input disabled value="user@example.com" />
        </FormGroup>
        <FormGroup>
          <Label>닉네임:</Label>
          <Input
            type="text"
            value={nickname}
            onChange={(e) => setNickname(e.target.value)}
          />
        </FormGroup>
        <FormGroup>
          <Label>성별:</Label>
          <GenderSelect disabled>
            <option value="male">남자</option>
            <option value="female">여자</option>
          </GenderSelect>
        </FormGroup>
        <FormGroup>
          <Label>생년월일:</Label>
          <BirthInputGroup>
            <BirthInput
              type="number"
              value={birthYear}
              onChange={(e) => setBirthYear(e.target.value)}
            />
            <BirthInput
              type="number"
              value={birthMonth}
              onChange={(e) => setBirthMonth(e.target.value)}
            />
            <BirthInput
              type="number"
              value={birthDay}
              onChange={(e) => setBirthDay(e.target.value)}
            />
          </BirthInputGroup>
        </FormGroup>
        <Button type="submit">수정</Button>
        <CancelButton type="button">취소</CancelButton>
      </Form>
    </Container>
  );
};


const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const Title = styled.h2`
  margin-bottom: 20px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  width: 300px;
`;

const FormGroup = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 15px;
`;

const Label = styled.label`
  width: 100px;
  font-weight: bold;
`;

const Input = styled.input`
  flex: 1;
  padding: 5px;
  border: 1px solid #ccc;
  border-radius: 3px;
`;

const GenderSelect = styled.select`
  flex: 1;
  padding: 5px;
  border: 1px solid #ccc;
  border-radius: 3px;
`;

const BirthInputGroup = styled.div`
  display: flex;
`;

const BirthInput = styled(Input)`
  width: 60px;
  margin-right: 5px;
`;

const ButtonGroup = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 20px;
`;

const Button = styled.button`
  padding: 10px;
  background-color: #007bff;
  color: #fff;
  border: none;
  border-radius: 3px;
  cursor: pointer;
  margin-right: 10px;
`;

const CancelButton = styled(Button)`
  background-color: #bdbdbd;
`;

export default EditProfile;







